    useEffect(() => {
        fetch("https://api.propublica.org/congress/v1/116/senate/members.json", {
            method: "GET",
            headers: {
                "X-API-Key": "AYZVqN2QlJkxBhkzZ4JsFd9J3cZG1SuoWNee9QoS"
            }
        })
            .then(res => res.json())
            .then(
                (result) => {
